import { Component } from '@angular/core';
import { ApiService } from 'src/app/services/apiservice.service';
import { NgxSpinnerService } from 'ngx-spinner';
import { ToastrService } from 'ngx-toastr';

@Component({
  selector: 'app-exchangerates',
  templateUrl: './exchangerates.component.html',
  styleUrls: ['./exchangerates.component.css']
})
export class ExchangeratesComponent {
  exchangeRates: any = {};
  loading: boolean = false;

  constructor(private apiService: ApiService, private spinner: NgxSpinnerService, private toastr: ToastrService,) {}

  ngOnInit(): void {
    this.refreshRates();
  }
  refreshRates(): void {
    this.loading = true;
    // For USD
    this.apiService.getExchangeRate('USD').subscribe(rate => {
      this.exchangeRates.USD = rate;
      this.loading = false;
    }, 
    (error) => {
      this.toastr.error(`The following error has occurred ${error}`);
      this.loading = false;
    }
  );
  this.loading = true;
  this.spinner.show();
    // For BRL
    this.apiService.getExchangeRate('BRL').subscribe(rate => {
      this.exchangeRates.BRL = rate;
      this.loading = false;
      this.loading = false;
    }, 
      (error) => {
        this.spinner.hide();
        this.loading = false;
      }
    );
  }

  this.spinner.show(undefined, {
    type: 'ball-spin-clockwise',
    size: 'medium',
    bdColor: 'rgba(255,255,255,0.7)',
    color: 'red'
  });
}
<ngx-spinner></ngx-spinner>

<div *ngIf="!loading">
  <h2>Exchange Rates</h2>
  <p>USD: {{ exchangeRates.USD }}</p>
  <p>BRL: {{ exchangeRates.BRL }}</p>
  <button (click)="refreshRates()">Refresh</button>
</div>
